a = 14
b = 3
print(a + b)   # addition
# Output = 17
print(a - b)   # subtraction
# Output = 11
print(a * b)   # multiplication
# Output = 42
print(a / b)   # true division
# Output = 4.666666666666667
print(a // b)   # integer division
# Output = 4
print(a % b)   # modulo operation (reminder of division)
# Output = 2
print(a ** b)   # power operation
# Output = 2744

print(-7/4)
print(-7//4)
# ability to add underscores within number literals (between digits or base specifiers, but not leading or trailing).
print(0x_4_0_0) # Output = 1024
print(1_024) # Output = 1024
